/**
 * Unify Telegram message & callback into one shape:
 * { chat_id, text, data, is_callback, username, message_id, _raw }
 */
const m = $json.message || {};
const c = $json.callback_query || {};
const isCb = !!c.data;

const chat_id = isCb ? c.message.chat.id : (m.chat?.id);
const text = (m.text || '').trim();
const data = isCb ? c.data : '';
const message_id = isCb ? c.message.message_id : (m.message_id || null);
const username = (m.from?.username || c.from?.username || '') || '';

return [{ json: { chat_id, text, data, is_callback: isCb, message_id, username, _raw: $json } }];

const now = new Date().toISOString();
return [{
  json: {
    CustomerId: $json.chat_id,
    Username: $json.username || '',
    LastSeenAt: now,
    FirstSeenAt: now // will only be used on first insert
  }
}];

const rows = $items('GS: Read Customer').map(i=>i.json);
const id = $items('Normalize Event')[0].json.chat_id;
const idx = rows.findIndex(r => String(r.CustomerId) === String(id));
const existing = idx >= 0 ? rows[idx] : null;
return [{ json: { exists: !!existing, rowIndex: idx+2 /* +2 for header+1-based */, existing } }];

const rows = $items('GS: Read State').map(i=>i.json);
const chat_id = $items('Normalize Event')[0].json.chat_id;
const idx = rows.findIndex(r => String(r.chat_id) === String(chat_id));
const exists = idx >= 0;
const rowIndex = idx+2; // header + 1-based
return [{ json: { chat_id, exists, rowIndex } }];

// Parse Callback â€” robust
// - Supports "ADD|SKU" and "KIND|ACTION|SKU"
// - Harvests callback query id (cqid) from multiple shapes including _raw

const u    = $json;
const raw  = u._raw ?? u;  // your Normalize Event keeps the original payload here
const cb   = raw.callback_query || raw.callbackQuery || raw.callback || null;
const msg  = cb?.message ?? raw.message ?? u.message ?? u;

// Sender / chat
const from = cb?.from ?? msg?.from ?? {};
const chat = msg?.chat ?? {};

// Base fields
const chat_id   = String(chat.id ?? u.chat_id ?? from.id ?? '');
const username  = (from.username ?? u.username ?? '') || '';
const text      = (u.text ?? msg?.text ?? '').trim();

// Callback payload + ID
const data = (u.data ?? cb?.data ?? raw.callback_data ?? '').trim();

// Callback query id (various shapes)
const cqid =
  u.cqid ??
  u.query_id ??
  u.callback_query_id ??
  raw?.query_id ??
  raw?.callback_query_id ??
  cb?.id ??
  null;

const message_id = Number(msg?.message_id ?? u.message_id ?? 0) || 0;

// Parse action/sku
let kind   = '';
let action = '';
let sku    = '';

// âœ… NEW: mark plain text messages explicitly as 'text'
if (!data && text) {
  kind = 'text';
}

if (data) {
  const parts = data.split('|');
  if (parts.length >= 3) {
    [kind, action, sku] = parts;        // KIND|ACTION|SKU
  } else if (parts.length === 2) {
    [action, sku] = parts;               // ACTION|SKU
    kind = 'BTN';
  } else {
    kind = 'BTN';
    action = parts[0] || '';
  }
}

// Optional passthroughs
const page = u.page ?? null;
const arg  = u.arg  ?? null;

// Mark as callback if we have an id OR callback data
const is_callback = Boolean(cqid || data);

return [{
  json: {
    chat_id, username, text, data,
    kind, action, sku, page, arg,
    message_id, cqid, is_callback
  }
}];

/**
 * Build Menu Reply â€” Category-first UX
 * - Default view: list categories (with item counts)
 * - Category view: list items in the chosen category + pagination
 * - Respects your existing Parse Callback shape and button styles
 */

const PAGE_SIZE = 6;
const CURRENCY = 'â‚µ';
const HEADER_CATS = 'ğŸ½ï¸ *Browse by Category*';
const HEADER_ITEMS = 'ğŸ½ï¸ *Todayâ€™s Menu*';
const MAX_LABEL_CHARS = 40;
const md = s => String(s ?? '').replace(/([_*\[\]()~`>#+\-=|{}.!])/g, '\\$1');

const clean = v => (v ?? '').toString().trim();
const isTrue = v => {
  const s = clean(v).toLowerCase();
  return s === 'true' || s === '1' || v === true || v === 1;
};
const priceFmt = v => {
  const n = parseFloat(v);
  return Number.isFinite(n) ? `${{CURRENCY}}${{n.toFixed(2)}}` : `${{v}}`;
};
const trimLabel = (s, n = MAX_LABEL_CHARS) => {
  const t = clean(s);
  return t.length > n ? t.slice(0, n - 1) + 'â€¦' : t;
};

const enc = s => clean(s).replace(/\|/g, '/');
const dec = s => clean(s);

function fromNode(name) {
  try {
    const a = $items(name);
    if (Array.isArray(a) && a.length) return a[0].json;
  } catch (_) {}
  return undefined;
}

const ctx = fromNode('Parse Callback') || fromNode('Default Menu Page') || $json;

const chat_id =
  ctx?.chat_id ??
  ctx?.message?.chat?.id ??
  ctx?.callback_query?.message?.chat?.id;

if (!chat_id) throw new Error('Build Menu Reply: no chat_id found.');

const rows = $input.all().map(i => i.json);

const available = rows.filter(r => {
  if ('Active' in r) return isTrue(r.Active);
  if ('Available' in r) return isTrue(r.Available);
  return true;
});

const seen = new Set();
const unique = [];
for (const r of available) {
  const key = clean(r.SKU) || `${{clean(r.Dish)}}|${{clean(r.Price)}}`;
  if (!seen.has(key)) { seen.add(key); unique.push(r); }
}

const groups = {};
for (const r of unique) {
  const cat = clean(r.Category) || 'Menu';
  (groups[cat] ||= []).push(r);
}
Object.values(groups).forEach(list =>
  list.sort((a, b) => clean(a.Dish).localeCompare(clean(b.Dish)))
);
const catNames = Object.keys(groups).sort((a, b) => a.localeCompare(b));
const counts = Object.fromEntries(catNames.map(c => [c, groups[c].length]));

const kind   = clean(ctx.kind);
const action = clean(ctx.action);
const data   = clean(ctx.data);
let token    = clean(ctx.sku);
let page     = 1;
let catSel   = '';

if (data === 'MENU|CATS') {
} else if (kind === 'CAT' && action === 'OPEN') {
  catSel = dec(token);
  page = 1;
} else if (kind === 'CAT' && action === 'PAGE') {
  const [rawCat, p] = token.split('::');
  catSel = dec(rawCat);
  page = Math.max(1, Number(p || 1));
}

if (!catSel) {
  if (!catNames.length) {
    return [{
      json: {
        chat_id,
        text: `${{HEADER_CATS}}

_No dishes available right now. Please check back later._`,
        parse_mode: 'Markdown',
        reply_markup: {
          inline_keyboard: [
            [{ text: 'ğŸ”„ Refresh', callback_data: 'MENU|REFRESH' }],
            [
              { text: 'ğŸ§º View Cart',  callback_data: 'CART|VIEW' },
              { text: 'âœ… Checkout',   callback_data: 'CART|CHECKOUT' },
              { text: 'ğŸ§¹ Clear',      callback_data: 'CART|CLEAR' }
            ]
          ]
        }
      }
    }];
  }

  let text = `${{HEADER_CATS}}

_Select a category:_
`;
  for (const c of catNames) {
    text += `â€¢ ${{md(c)}} (${{counts[c]}})
`;
  }

  const kb = [];
  for (const c of catNames) {
    kb.push([{ text: `${{c}} (${{counts[c]}})`, callback_data: `CAT|OPEN|${{enc(c)}}` }]);
  }
  kb.push([
    { text: 'ğŸ§º View Cart',  callback_data: 'CART|VIEW' },
    { text: 'âœ… Checkout',   callback_data: 'CART|CHECKOUT' },
    { text: 'ğŸ§¹ Clear',      callback_data: 'CART|CLEAR' }
  ]);

  return [{ json: { chat_id, text, parse_mode: 'Markdown', reply_markup: { inline_keyboard: kb } } }];
}

const inCat = (groups[catSel] || []).map(r => {
  const emoji = clean(r.Emoji);
  const dishName = clean(r.Dish) || clean(r.ButtonLabel) || clean(r.SKU);
  const baseRaw = [emoji, dishName].filter(Boolean).join(' ').trim();
  return {
    sku: clean(r.SKU) || `${{clean(r.Dish)}}|${{clean(r.Price)}}`,
    itemBtn: `${{trimLabel(baseRaw)}} â€” ${{priceFmt(r.Price)}}`,
    bodyLine: `â€¢ ${{md(baseRaw)}} â€” ${{priceFmt(r.Price)}}`
  };
});

if (!inCat.length) {
  return [{
    json: {
      chat_id,
      text: `*${{HEADER_ITEMS}}*

_No dishes in "${{md(catSel)}}" right now._`,
      parse_mode: 'Markdown',
      reply_markup: {
        inline_keyboard: [
          [{ text: 'ğŸ”™ Categories', callback_data: 'MENU|CATS' }],
          [
            { text: 'ğŸ§º View Cart',  callback_data: 'CART|VIEW' },
            { text: 'âœ… Checkout',   callback_data: 'CART|CHECKOUT' },
            { text: 'ğŸ§¹ Clear',      callback_data: 'CART|CLEAR' }
          ]
        ]
      }
    }
  }];
}

const totalItems = inCat.length;
const totalPages = Math.max(1, Math.ceil(totalItems / PAGE_SIZE));
page = Math.min(Math.max(1, page), totalPages);
const start = (page - 1) * PAGE_SIZE;
const pageItems = inCat.slice(start, start + PAGE_SIZE);

let text = `${{HEADER_ITEMS}}

*${{md(catSel)}}*
`;
for (const it of pageItems) text += it.bodyLine + '
';
text += `
_Page ${{page}} of ${{totalPages}}_
_Tap an item to add to cart_`;

const kb = [];
for (const it of pageItems) {
  kb.push([{ text: it.itemBtn, callback_data: `ADD|${{it.sku}}` }]);
  kb.push([{ text: 'â„¹ï¸',       callback_data: `DETAILS|${{it.sku}}` }]);
}

const nav = [];
if (page > 1)        nav.push({ text: 'â¬…ï¸ Prev', callback_data: `CAT|PAGE|${{enc(catSel)}}::${{page - 1}}` });
nav.push({ text: 'ğŸ”™ Categories', callback_data: 'MENU|CATS' });
if (page < totalPages) nav.push({ text: 'Next â¡ï¸', callback_data: `CAT|PAGE|${{enc(catSel)}}::${{page + 1}}` });
kb.push(nav);

kb.push([
  { text: 'ğŸ§º View Cart',  callback_data: 'CART|VIEW' },
  { text: 'âœ… Checkout',   callback_data: 'CART|CHECKOUT' },
  { text: 'ğŸ§¹ Clear',      callback_data: 'CART|CLEAR' }
]);

return [{ json: { chat_id, text, parse_mode: 'Markdown', reply_markup: { inline_keyboard: kb } } }];

// Build Dish Details â€” sendPhoto (preferred) or sendMessage (fallback)
const CURRENCY = 'â‚µ';
const md = s => String(s ?? '').replace(/([_*\[\]()~`>#+\-=|{}.!])/g, '\\$1');

const parse = $items('Parse Callback')[0]?.json || $json;
const sku = parse.sku || $json.sku;
const chat_id = parse.chat_id || $json.chat_id;

const rows = $input.all().map(i => i.json);
const row = rows.find(r => String(r.SKU) === String(sku));

if (!row) {
  return [{
    json: {
      _endpoint: 'sendMessage',
      payload: { chat_id, text: 'Item not found.' }
    }
  }];
}

const price = Number(row.Price) || 0;
const emoji = md(row.Emoji || '').trim();
const dishName = md(row.Dish || row.ButtonLabel || row.SKU || '');
const namePart = [emoji, dishName].filter(Boolean).join(' ').trim();
const notes = row.Notes ? `
_${{md(row.Notes)}}_` : '';
const caption = `*${{namePart}} â€” ${{CURRENCY}}${{price}}*${{notes}}`;

const kb = {
  inline_keyboard: [
    [{ text: `â• Add ${row.Dish || row.SKU || ''}`, callback_data: `ADD|${sku}` }],
    [{ text: 'â¬…ï¸ Back', callback_data: 'MENU|REFRESH' }]
  ]
};

if (row.PhotoURL) {
  return [{
    json: {
      _endpoint: 'sendPhoto',
      payload: {
        chat_id,
        photo: row.PhotoURL,
        caption,
        parse_mode: 'Markdown',
        reply_markup: kb
      }
    }
  }];
}

return [{
  json: {
    _endpoint: 'sendMessage',
    payload: {
      chat_id,
      text: caption,
      parse_mode: 'Markdown',
      reply_markup: kb
    }
  }
}];

// Pick Menu Item (by SKU)
// Mode: Run once for all items

// All menu rows from GS: Read Menu
const menu = $input.all().map(i => i.json);

// SKU the user actually tapped
const cb = ($items('Parse Callback')?.[0]?.json) || {};
const chat_id = String(cb.chat_id || '');
const wanted = String(cb.sku || '').trim().toUpperCase();
if (!chat_id || !wanted) return []; // nothing to do

// Find the matching row in the menu
const row = menu.find(r =>
  String(r.SKU || '').trim().toUpperCase() === wanted
);
if (!row) return []; // SKU not in menu

const price = Number(row.Price || 0);
const dish  = row.Dish ?? row.ButtonLabel ?? wanted;

// Emit exactly one new cart line
return [{
  json: {
    chat_id,
    LineId: `${chat_id}|${wanted}`,
    SKU: wanted,
    Dish: dish,
    Price: price,
    Quantity: 1,
    LineTotal: price,
    Frozen: '',
    CurrentStep: '',
    PaymentStatus: '',
    OrderNumber: '',
    OrderUID: ''
  }
}];

// Input: the single â€œpicked menu item by SKUâ€ (current add) is $json
// Read all cart rows from the previous GS: Read Cart (add)
const rows = $items('GS: Read Cart (add)').map(i => i.json);

const chatId = String($json.chat_id);
const sku    = String($json.SKU);

// â€œOpenâ€ = NOT frozen, NOT paid, NO order number
const isOpen = r =>
  String(r.chat_id) === chatId &&
  String(r.SKU)     === sku &&
  !String(r.Frozen ?? '').toLowerCase().startsWith('t') &&
  !String(r.PaymentStatus ?? '').toUpperCase().includes('PAID') &&
  !String(r.OrderNumber ?? '').trim();

const open = rows.filter(isOpen);

// If we have an existing open row, keep its row_number to update.
// (If you want newest, sort by Timestamp and take last)
if (open.length) {
  const keep = open[open.length - 1];
  return [{
    json: {
      ...keep,
      exists: true,
      keep_row_number: keep.row_number   // <-- weâ€™ll match updates on this
    }
  }];
}

return [{
  json: {
    exists: false,
    chat_id: chatId,
    SKU: sku,
    // Carry through what your downstream nodes need
    Dish: $json.Dish,
    Price: $json.Price,
    Quantity: 1,
    LineTotal: Number($json.Price || 0)
  }
}];

// n8n Code node: Build Cart Summary (old callbacks, new formatting)
// - Keeps your original callbacks: CART|CHECKOUT, CART|CLEAR, MENU|OPEN
// - Same cart filtering (this user only, qty>0, not frozen, not PAID)
// - Shows *line totals* per item (qty * price or saved LineTotal)
// - Subtotal = sum of line totals; Delivery fee stays 0.00 here

const NBSP = 'Â ';
const WIDTH = 44;
const money = v => `â‚µ${{Number(v || 0).toFixed(2)}}`;
const asInt = (v, d = 0) => {
  const n = Number(v);
  return Number.isFinite(n) ? n : d;
};
const asNum = (v, d = 0) => {
  const n = Number(v);
  return Number.isFinite(n) ? n : d;
};
const md = s => String(s ?? '').replace(/([_*\[\]()~`>#+\-=|{}.!])/g, '\\$1');
const visLen = s => String(s).replace(/\.|\*|_/g, '').length;
const padTail = s => s + NBSP.repeat(Math.max(0, WIDTH - visLen(s)));

const ctxItem = $items('Parse Callback')?.[0]?.json ?? {};
const chat_id = String(ctxItem.chat_id ?? $json.chat_id ?? '');
if (!chat_id) {
  return [{
    json: { method: 'noop', payload: { reason: 'missing chat_id in Build Cart Summary' } }
  }];
}

const allRows = $input.all().map(i => i.json);

const rows = allRows.filter(r => {
  if (!r || !r.SKU) return false;
  if (String(r.chat_id ?? '') !== chat_id) return false;
  const qty = asInt(r.Quantity, 0);
  if (qty <= 0) return false;
  const fr = String(r.Frozen ?? '').trim().toLowerCase();
  if (fr === 'true' || fr === '1' || fr === 'yes') return false;
  if (String(r.PaymentStatus ?? '').trim().toUpperCase() === 'PAID') return false;
  return true;
});

if (rows.length === 0) {
  const text = [
    'ğŸ§º *Your Cart*',
    '',
    '_Your cart is empty._',
    '',
    'Tap *View Menu* to add items.'
  ].join('
');

  const keyboard = [
    [{ text: 'ğŸ“– View Menu', callback_data: 'MENU|OPEN' }]
  ];

  return [{
    json: {
      method: 'sendMessage',
      payload: {
        chat_id,
        text,
        parse_mode: 'Markdown',
        reply_markup: { inline_keyboard: keyboard }
      }
    }
  }];
}

let subtotal = 0;

const itemLines = rows.map(r => {
  const dish  = md(String(r.Dish ?? r.SKU ?? 'â€”'));
  const qty   = asInt(r.Quantity, 0);
  const unit  = asNum(r.Price, 0);
  const lineTotal = asNum(r.LineTotal, qty * unit);
  subtotal += lineTotal;
  return padTail(`â€¢ ${{dish}} x${{qty}} â€” ${{money(lineTotal)}}`);
});

const deliveryFee = 0;
const total = subtotal + deliveryFee;

const keyboard = [
  [{ text: 'ğŸ§¾ Checkout', callback_data: 'CART|CHECKOUT' }],
  [
    { text: 'ğŸ§¹ Clear cart', callback_data: 'CART|CLEAR' },
    { text: 'ğŸ“– View Menu',  callback_data: 'MENU|OPEN'  }
  ]
];

const text = [
  padTail('ğŸ§º *Your Cart*'),
  '',
  ...itemLines,
  '',
  '',
  padTail(`*Subtotal:* ${{money(subtotal)}}`),
  padTail(`*Delivery fee:* ${{money(deliveryFee)}}`),
  padTail(`*Total:* ${{money(total)}}`)
].join('
');

return [{
  json: {
    method: 'sendMessage',
    payload: {
      chat_id,
      text,
      parse_mode: 'Markdown',
      reply_markup: { inline_keyboard: keyboard }
    }
  }
}];

// Collect Rows To Delete â€” delete ONLY unpaid & unfrozen lines for this user

const ctx = $items('Parse Callback')?.[0]?.json ?? {};
const chat_id = String(ctx.chat_id ?? $json.chat_id ?? '');

const rows = $input.all().map(i => i.json).filter(r => String(r.chat_id ?? '') === chat_id);

// helpers
function isPaid(v) {
  const s = String(v ?? '').trim().toLowerCase();
  // allow common synonyms/flags
  return ['paid','success','successful','confirmed','complete','completed','true','yes','1'].includes(s);
}
function isTrue(v) {
  const s = String(v ?? '').trim().toLowerCase();
  return ['true','1','yes'].includes(s);
}

// keep only rows we should delete: NOT paid and NOT frozen
const toDelete = rows
  .filter(r => !isPaid(r.PaymentStatus) && !isTrue(r.Frozen))
  .map(r => Number(r.row_number))
  .filter(n => Number.isFinite(n))
  // delete bottom-up to avoid row-shift
  .sort((a, b) => b - a);

if (toDelete.length === 0) {
  // Signal "nothing to delete" to your IF node
  return [{ json: { chat_id, none: true } }];
}

// emit one item per row_number
return toDelete.map(n => ({ json: { chat_id, row_number: n } }));

// Fan out dupes array to items with row_number
const chat_id = $json.chat_id;
const dupes = Array.isArray($json.dupes) ? $json.dupes : [];
if (!dupes.length) return [];
return dupes.map(n => ({ json: { chat_id, row_number: Number(n) } }));

/**
 * Build Checkout Summary (Delivery/Pickup aware)
 * Input: items from "Attach State â†’ Lines (checkout)" (cart lines with state fields attached)
 * Output: { method, payload } for Telegram
 */

const CURRENCY = 'â‚µ';
const WIDTH = 40;
const NBSP = 'Â ';
const md = s => String(s ?? '').replace(/([_*\[\]()~`>#+\-=|{}.!])/g, '\\$1');

const money  = n => `${{CURRENCY}}${{(Number(n)||0).toFixed(2)}}`;
const visLen = s => String(s).replace(/\.|\*|_/g,'').length;
const pad    = s => s + NBSP.repeat(Math.max(0, WIDTH - visLen(s)));

const rows = $input.all().map(i => i.json);

const chat_id = String(
  rows[0]?.chat_id ?? rows[0]?.ChatID ?? $json._chat_id ?? $json.chat_id ?? ''
);

if (!rows.length || !chat_id) {
  return [{
    json: {
      method: 'sendMessage',
      payload: {
        chat_id,
        text: 'ğŸ§º Your cart is empty.

Tap *Menu* to add items.',
        parse_mode: 'Markdown',
        reply_markup: { inline_keyboard: [[{ text: 'ğŸ½ï¸ Back to Menu', callback_data: 'MENU|REFRESH' }]] }
      }
    }
  }];
}

const fulfillmentRaw = String(rows[0]?.Fulfillment || '').toLowerCase().trim();
const address  = String(rows[0]?.Address  || '').trim();
const zone     = String(rows[0]?.Zone     || '').trim();
const phone    = String(rows[0]?.Phone    || '').trim();
const feeInput = Number(rows[0]?.DeliveryFee ?? 0);
const isPickup   = fulfillmentRaw === 'pickup';
const isDelivery = fulfillmentRaw === 'delivery';
const fee        = isPickup ? 0 : (Number.isFinite(feeInput) ? feeInput : 0);

const safeAddress = address ? md(address) : '';
const safeZone = zone ? md(zone) : '';
const safePhone = phone ? md(phone) : '';

const header = pad('ğŸ§º *Your Cart*');
const lines = rows.map(r => {
  const qty   = Number(r.Quantity ?? r.qty ?? 1) || 1;
  const price = Number(r.Price ?? r.price ?? 0)  || 0;
  const name  = md(String(r.Dish ?? r.name ?? r.SKU ?? '').trim());
  const ltRaw = Number(r.LineTotal ?? r.line_total);
  const lineTotal = (Number.isFinite(ltRaw) && ltRaw > 0) ? ltRaw : qty * price;
  return pad(`â€¢ ${{name}} x${{qty}} â€” ${{money(lineTotal)}}`);
});

const subtotal = rows.reduce((s, r) => {
  const lt = Number(r.LineTotal ?? r.line_total);
  if (Number.isFinite(lt) && lt > 0) return s + lt;
  const q = Number(r.Quantity ?? r.qty ?? 1) || 1;
  const p = Number(r.Price ?? r.price ?? 0)  || 0;
  return s + q * p;
}, 0);

const fulfillmentLine = isPickup
  ? 'ğŸª Pickup at counter'
  : (isDelivery
     ? `ğŸšš Delivery${{safeAddress ? ` to: ${{safeAddress}}` : ''}}${{safeZone ? ` (Zone: ${{safeZone}})` : ''}}`
     : 'â€” choose delivery or pickup below â€”');

const meta = [
  pad(`*Fulfillment:* ${{fulfillmentLine}}`),
  safePhone ? pad(`*Phone:* ${{safePhone}}`) : null
].filter(Boolean);

const foot = [
  pad(`*Subtotal:* ${{money(subtotal)}}`),
  pad(`*Delivery fee:* ${{money(fee)}}`),
  pad(`*Total:* ${{money(subtotal + fee)}}`)
];

const text = [header, '', ...lines, '', ...meta, '', ...foot].join('
');

const baseRows = [
  [
    { text: 'ğŸ§¹ Clear', callback_data: 'CART|CLEAR' },
    { text: 'ğŸ½ï¸ Continue Shopping', callback_data: 'MENU|REFRESH' }
  ]
];

let keyboard;
if (!isPickup && !isDelivery) {
  keyboard = [
    [
