const md = s => String(s ?? '').replace(/([_*\[\]()~`>#+\-=|{}.!])/g, '\\$1');
const chat_id = $json.chat_id;

const text = [
  'ğŸª *Pickup selected.*',
  'Please reply with your *phone number* (e.g., `0241234567` or `+233241234567`).'
].join('
');

return [{
  json: {
    method: 'sendMessage',
    payload: {
      chat_id,
      text,
      parse_mode: 'Markdown',
      reply_markup: {
        inline_keyboard: [
          [{ text: 'Change to Delivery', callback_data: 'FULFILL|DELIVERY' }]
        ]
      }
    }
  }
}];

/**
 * Attach State â†’ Lines (checkout) â€” robust chat_id + frozen filter
 * Joins the single UserStates row onto each cart line so the summary can
 * see Fulfillment / Address / DeliveryFee / Phone.
 *
 * Inputs (by name â€“ but we also fall back to $input when run alone):
 *   - GS: Read Cart (checkout)   â†’ cart lines
 *   - GS: Read State (summary)   â†’ all UserStates rows
 */

function fromNode(name) {
  try { return $items(name).map(i => i.json); }
  catch (_) { return []; }
}

// 1) Collect inputs
let cartAll   = fromNode('GS: Read Cart (checkout)');
let statesAll = fromNode('GS: Read State (summary)');

// Fallback when executing this node alone
if (cartAll.length === 0 || statesAll.length === 0) {
  const ins = $input.all().map(i => i.json);
  if (cartAll.length === 0) {
    cartAll = ins.filter(r => r && (r.SKU || r.sku || r.Dish));
  }
  if (statesAll.length === 0) {
    statesAll = ins.filter(r =>
      r && (
        r.chat_id !== undefined || r.ChatID !== undefined ||
        r.Fulfillment !== undefined || r.CurrentStep !== undefined ||
        r.Address !== undefined || r.Zone !== undefined ||
        r.DeliveryFee !== undefined || r.Phone !== undefined
      )
    );
  }
}

// 2) Drop frozen lines first
const isFrozen = v => {
  const t = String(v ?? '').trim().toLowerCase();
  return t === 'true' || t === '1' || t === 'yes';
};
const cart = cartAll.filter(r => !isFrozen(r.Frozen));

// 3) Resolve chat_id AFTER filtering (and with fallbacks)
const chatIdCandidates = [
  ...cart.map(r => r?.chat_id ?? r?.ChatID),
  ...statesAll.map(s => s?.chat_id ?? s?.ChatID),
  $json._chat_id, $json.chat_id
].map(v => (v == null ? '' : String(v))).filter(v => v && v !== 'empty');

const chatId = chatIdCandidates[0] || '';

// 4) Pick this user's state row (or default)
const state = statesAll.find(s => String(s?.chat_id ?? s?.ChatID) === chatId) || {};
const s = {
  chat_id: chatId,
  Fulfillment: String(state.Fulfillment || '').toLowerCase(),
  Address: state.Address || '',
  Zone: state.Zone || '',
  DeliveryFee: Number(state.DeliveryFee || 0) || 0,
  Phone: state.Phone || ''
};

// 5) Emit one item per active cart line with state attached
const out = cart.map(r => ({ json: { ...r, ...s } }));
return out.length ? out : [];

const price = Number($json.Price || 0);
const current = Number($json.Quantity || 0);
const qty = current + 1;

return [{
  json: {
    ...$json,
    Quantity: qty,
    LineTotal: qty * price
  }
}];

const frozen = String($json.Frozen ?? '').toLowerCase() === 'true';
const paid   = String($json.PaymentStatus ?? '').toUpperCase() === 'PAID';
const hasOrderNo = String($json.OrderNumber ?? '').trim() !== '';

/**
 * Drop the item if it's a paid/frozen line or already has an order number.
 * Returning [] in n8n means â€œdonâ€™t pass this item forwardâ€.
 */
if (frozen || paid || hasOrderNo) {
  return [];
}
return [$json];

// Cart lines from the combine gate
const cartItems = $input.all();

// Meta from Ensure Order Number (now guaranteed executed)
let meta = {};
try { meta = ($items('Ensure Order Number')[0]?.json) || {}; }
catch { meta = ($node['Ensure Order Number']?.json) || {}; }

const out = cartItems.map(i => {
  const j = { ...i.json };
  if (meta.OrderNumber) j.OrderNumber = meta.OrderNumber;
  if (meta.OrderUID)    j.OrderUID    = meta.OrderUID;
  if (meta.Zone !== undefined)        j.Zone        = meta.Zone;
  if (meta.DeliveryFee !== undefined) j.DeliveryFee = Number(meta.DeliveryFee);
  return { json: j };
});

return out; // array

// Fan-out each order line that is not already PAID.
// Input: one aggregated item with { lines: [...] }.
const all = $input.all();
if (!all.length) return [];

const agg = (all[0].json || {});
const lines = Array.isArray(agg.lines) ? agg.lines : [];

// Idempotency: skip rows already marked PAID
const toUpdate = lines.filter(l =>
  String(l.PaymentStatus || '').toUpperCase() !== 'PAID'
);

// Emit one minimal item per line for the Sheets update
return toUpdate.map(l => ({
  json: {
    row_number: Number(l.row_number),
    CurrentStep: 'paid',
    PaymentStatus: 'PAID',
  },
}));

const md = s => String(s ?? '').replace(/([_*\[\]()~`>#+\-=|{}.!])/g, '\\$1');
const brand = md($env.BRAND_NAME || 'Sefake Kitchen');
const momoRaw  = $env.MOMO_NUMBER || '';
const momo  = momoRaw ? md(momoRaw) : '';

const lines = [
  `ğŸ‘‹ Welcome to *${{brand}}*!`,
  `Order delicious meals via Telegram.`,
  ``,
  `â€¢ Tap *Menu* to start an order`,
  `â€¢ Already have a cart? Tap *Checkout*`,
  ...(momo ? [`â€¢ MoMo: ${{momo}}`] : []),
];

const text = lines.join('
');
const out = [];

for (const it of $input.all()) {
  const j = it.json || {};
  const chat_id = j.chat_id;

  const reply_markup = {
    inline_keyboard: [
      [{ text: 'ğŸ“– Menu',     callback_data: 'MENU|OPEN' }],
      [{ text: 'ğŸ§¾ Checkout', callback_data: 'CART|CHECKOUT' }]
    ]
  };

  out.push({
    json: {
      method: 'sendMessage',
      payload: {
        chat_id,
        text,
        parse_mode: 'Markdown',
        disable_web_page_preview: true,
        reply_markup
      }
    }
  });
}

return out;

// ====== Config via env (safe defaults) ======
const OPEN_HHMM  = $env.OPEN_HHMM   || '09:00';                 // 24h
const CLOSE_HHMM = $env.CLOSE_HHMM  || '21:00';
const OPEN_DAYS  = ($env.OPEN_DAYS  || 'Mon,Tue,Wed,Thu,Fri,Sat')
  .split(',')
  .map(s => s.trim().toLowerCase());                            // ['mon', ...]
const TZ         = $env.TIMEZONE    || 'Africa/Accra';
const HOLIDAYS   = ($env.HOLIDAYS   || '')                      // '2025-12-25,2025-12-26'
  .split(',')
  .map(s => s.trim())
  .filter(Boolean);

// ====== Helpers ======
function inWindow(now, open, close) {
  // open/close format 'HH:MM'
  const [oh, om] = open.split(':').map(Number);
  const [ch, cm] = close.split(':').map(Number);
  const start = new Date(now); start.setHours(oh, om, 0, 0);
  const end   = new Date(now); end.setHours(ch, cm, 0, 0);

  // overnight window (e.g., 18:00â€“02:00):
  if (end <= start) return (now >= start) || (now <= end);
  return (now >= start) && (now <= end);
}

const ev = $input.first()?.json ?? {};
// current time in business TZ
const now = new Date(new Date().toLocaleString('en-US', { timeZone: TZ }));
const dayKey = now.toLocaleDateString('en-US', { weekday: 'short' }).slice(0,3).toLowerCase(); // 'mon'
const isHoliday  = HOLIDAYS.includes(now.toISOString().slice(0,10));
const isOpenDay  = OPEN_DAYS.includes(dayKey);
const withinTime = inWindow(now, OPEN_HHMM, CLOSE_HHMM);

const is_open = isOpenDay && withinTime && !isHoliday;

// Return an item (array-of-items with a json object)
return [{
  json: {
    ...ev,                 // keep incoming chat_id, etc.
    is_open,
    tz: TZ,
    now_iso: now.toISOString(),
  }
}];

// Closed message (shows hours + days)
const md = s => String(s ?? '').replace(/([_*\[\]()~`>#+\-=|{}.!])/g, '\\$1');
const brand = md($env.BRAND_NAME || 'Sefake Kitchen');
const OPEN  = md($env.OPEN_HHMM  || '09:00');
const CLOSE = md($env.CLOSE_HHMM || '20:00');
const DAYS  = md(($env.OPEN_DAYS || 'Mon,Tue,Wed,Thu,Fri,Sat').split(',').map(s => s.trim()).join(', '));

const lines = [
  `â³ *${{brand}}* is currently *closed*.`,
  `ğŸ•’ *Hours:* ${{OPEN}}â€“${{CLOSE}}`,
  `ğŸ“… *Days:* ${{DAYS}}`,
  '',
  `You can still browse the menu. If youâ€™ve already paid, tap *I have paid* in your earlier message.`,
];

const text = lines.join('
');

const reply_markup = {
  inline_keyboard: [
    [{ text: 'ğŸ“– View menu',     callback_data: 'MENU|OPEN' }],
    [{ text: 'ğŸ§¾ Go to checkout', callback_data: 'CART|CHECKOUT' }],
  ]
};

return [{
  json: {
    method: 'sendMessage',
    payload: {
      chat_id: $json.chat_id,
      text,
      parse_mode: 'Markdown',
      reply_markup,
    }
  }
}];

/**
 * Ensure Cart Rows (approve) â€” single-output
 * IN: items from "GS: Read Cart (by ref) â€” APPROVE"
 * OUT: 
 *  - if rows exist -> each row wrapped as { json: {...} }
 *  - if none      -> ONE control item { json: { _notify_admin: true, ref, text } }
 */
function n(v){const x=Number(v);return Number.isFinite(x)?x:NaN;}

const rows = $input.all()
  .map(i => (i && i.json) ? i.json : {})
  .filter(r => Number.isFinite(n(r.row_number)) && n(r.row_number) > 0);

// Best-effort ref for message (from input rows)
const ref = String(rows[0]?.OrderNumber || '').trim();

if (rows.length === 0) {
  return [{
    json: {
      _notify_admin: true,
      ref,
      text: ref 
        ? `âš ï¸ No cart lines found for ref ${ref}. Nothing to approve.`
        : `âš ï¸ No cart lines found. Nothing to approve.`,
    }
  }];
}

// pass-through: one item PER cart row, correctly wrapped
return rows.map(r => ({ json: r }));

const raw = String($json.phone || $json.Phone || '').trim();
const digits = raw.replace(/\D/g,'');
const hasPhone = digits.length >= 9; // Ghana min length check
return hasPhone ? [] : [$json];

const prev = String($json._prevStep || '');
const cur  = String($json.CurrentStep || '');
if (cur === 'await_phone' && prev !== 'await_phone') return [$json];
return [];

// Auto-switch to edit endpoints on callbacks (no duplicate messages)
const isCb = $json.is_callback === true;
const mid  = $json.message_id;
const chat = $json.chat_id;
const p    = $json.payload || {};
const endpoint = ($json._endpoint || $json.method || '').toString();

function safeStr(v, fallback=' ') {
  const s = (v ?? '').toString();
  return s.length ? s : fallback; // Telegram hates empty strings
}

const out = { ...$json }; // preserve anything you already set

if (isCb && mid && chat) {
  // If the original builder wanted to send a photo (with caption),
  // prefer editing the caption. (If you need to change the image itself,
  // youâ€™d use editMessageMedia, but caption/buttons is enough here.)
  const isPhoto = endpoint === 'sendPhoto' || typeof p.photo === 'string';

  if (isPhoto) {
    out._endpoint = 'editMessageCaption';
    out.payload = {
      chat_id: chat,
      message_id: mid,
      caption: safeStr(p.caption),
      parse_mode: p.parse_mode || 'Markdown',
      reply_markup: p.reply_markup
    };
  } else {
    out._endpoint = 'editMessageText';
    out.payload = {
      chat_id: chat,
      message_id: mid,
      text: safeStr(p.text),
      parse_mode: p.parse_mode || 'Markdown',
      reply_markup: p.reply_markup,
      disable_web_page_preview: true
    };
  }
} else {
  // Non-callback: keep whatever the builder set, but normalise field name
  out._endpoint = endpoint || (p.photo ? 'sendPhoto' : 'sendMessage');
}

return [{ json: out }];

